def Secoundtab(window,canvas,button_image_viewDetails,image_image_Background,image_image_logo,button_image_Search,button_image_EnterDetail):
    

    text_root = canvas.create_text(
        330.0,
        367.0,
        anchor="nw",
        text='',
        fill="#ff1100",
        font=("InriaSans Regular", 16 * -1))


    image_logo = canvas.create_image(704,59,image = image_image_logo) 

    # Search Button
    def serach_root():
        canvas.itemconfig(text_root, text="") 
        name = entry_1_root.get()
        if len(name) == 0:
            canvas.itemconfig(text_root, text="You have fill the blanks") 
        else:
            target = name
            Scraper(window,target)
            os.system(f'python profile.py {target}')

            






    button_1_root = Button(
        image = button_image_Search,
        bg="#111645",
        activebackground = '#111645',
        borderwidth=0,
        highlightthickness=0,
        command=serach_root,
        relief="flat"
    )
    button_1_root.place(
        x=342.0,
        y=286.0,
        width=67.0,
        height=30.0
    )



    # Target Name
    #entry_image_1 = PhotoImage(
        #file=relative_to_assets_root("entry_1.png"))
    entry_bg_1_root = canvas.create_image(
        451.5,
        80.0,
        #image=entry_image_1
    )
    entry_1_root = Entry(
        bd=0,
        bg="#C4C4C4",
        highlightthickness=0
    )
    entry_1_root.place(
        x=275.0,
        y=250.0,
        width=195.0,
        height=28.0
    )
     # "Target Name"
    target_name=canvas.create_text(
        330.0,
        230.0,
        anchor="nw",
        text="Target Name",
        fill="#C4C4C4",
        font=("InriaSans Regular", 14 * -1)
    )



   

    



    









